import matplotlib.pyplot as plt
import numpy as np
import imageio
from google.colab import drive

# montowanie google drive
drive.mount('/content/drive')

# wczytanie danych
def load_data(name):
  with open('/content/drive/My Drive/Colab Notebooks/gameoflife/' + name) as f:
    return [float(i) for i in f.read().split()]

# wykres 2D
def plot2d_new(x, y, ax_label, ay_label, title):
  _, ax = plt.subplots()
  ax.plot(x, y)
  ax.grid()
  ax.set_title(title, fontsize=20)
  plt.xlabel(ax_label, fontsize=18) 
  plt.ylabel(ay_label, fontsize=18) 

  plt.show()

def plot_animation(name, data, heatmap):
  x = np.array(range(len(data)))
  y = np.array(range(len(data[0])))
  x = np.repeat(x, len(data[0]))
  y = np.tile(y, len(data))
  z = np.array(data).flatten()

  x_data = x
  y_data = y
  z_data = [0] * z.size
  dx = [1] * x_data.size
  dy = [1] * y_data.size
  dz = z

  if heatmap:
    fig = plt.figure(figsize=(24, 9))
    ax1 = fig.add_subplot(121)
    im1 = ax1.imshow(np.array(data), interpolation='None')
    fig.colorbar(im1, orientation='vertical')
  else:
    fig = plt.figure()
    ax1 = fig.add_subplot(projection='3d')
    ax1.set_zlim([0, 0.2])
    ax1.bar3d(x_data, y_data, z_data, dx, dy, dz, shade=True)

  ax1.set_title(name)
  
  ax1.set_xlim([0, 100])
  ax1.set_ylim([0, 100])
  
  plt.xlabel('x')
  plt.ylabel('y')
  #ax1.set_zlabel('length of life')
  fig.canvas.draw()       # draw the canvas, cache the renderer
  image = np.frombuffer(fig.canvas.tostring_rgb(), dtype='uint8')
  image  = image.reshape(fig.canvas.get_width_height()[::-1] + (3,))

  return image

def plot_probability_animation(data, fps, name, heatmap):
  kwargs_write = {'fps':fps, 'quantizer':'nq'}
  imageio.mimsave('./' + name + '.gif', [plot_animation('Probability - cycle number ' + str(cycle), data[cycle], heatmap) for cycle in range(len(data))], fps=fps)

def load_data_3d(src):
  data = load_data(src)
  cycle_number = np.array(data[0:][::4]).astype(int)
  dataX = np.array(data[1:][::4]).astype(int)
  dataY = np.array(data[2:][::4]).astype(int)
  dataZ = np.array(data[3:][::4])

  all_data = []
  for i in range(cycle_number[-1] + 1):
    all_data.append([])
    for j in range(dataX[-1] + 1):
      all_data[i].append([])
      for k in range(dataY[-1] + 1):
        all_data[i][j].append(dataZ[k + j * (dataY[-1] + 1) + i * (dataX[-1] + 1) * (dataY[-1] + 1)])
  return all_data

def sum_cells_to_cycles(data):
  sums = []
  for x in range(len(data)):
    sums.append(0.0)
    for y in range(len(data[x])):
      for z in range(len(data[x][y])):
        sums[x] += data[x][y][z]
  return sums

def calculate_entropy(data):
  probabilities = np.array(sum_cells_to_cycles(data))
  entropies = -(probabilities / (len(data[0]) * len(data[0][0]))) * np.log(probabilities / (len(data[0]) * len(data[0][0])))

  _, ax = plt.subplots()
  ax.plot(range(len(entropies)), entropies)
  ax.set_title("Entropy")
  ax.grid()
  plt.xlabel('Cycle')
  plt.ylabel('Entropy')
  plt.show()

def calculate_velocity(data):
  velocities = []
  for x in range(len(data) - 1):
    velocities.append(0)
    for y in range(len(data[x])):
      for z in range(len(data[x][y])):
        if(data[x][y][z] == 0 and data[x + 1][y][z] != 0):
          velocities[x] += 1
          
  return velocities

def calculate_acceleration(data):
  velocities = calculate_velocity(data)
  accelerations = np.array(velocities[1:-1]) - np.array(velocities[0:-2])
  return accelerations

def calculate_entropy_lattice(data):
  entropies = []
  for x in range(len(data)):
    entropies.append([])
    for y in range(len(data[x])):
      entropies[x].append([])
      for z in range(len(data[x][y])):
        if data[x][y][z] > 0:
          entropies[x][y].append(-np.log(data[x][y][z]))
        else:
          entropies[x][y].append(0.0)
  return entropies

def calculate_entropy2(data, fig_x = 6.4, fig_y = 4.8, show_etropy = True):
  entropies = calculate_entropy_lattice(data)
  entropy = sum_cells_to_cycles(entropies)

  if (show_etropy == False):
    return entropy
  _, ax = plt.subplots(figsize=(fig_x, fig_y))
  ax.plot(range(len(entropy)), entropy)
  ax.set_title("Entropy")
  ax.grid()
  plt.xlabel('Cycle')
  plt.ylabel('Entropy')
  plt.show()

